
public class Math {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 23;
		int z = 10;
		
		int k = x%z;
		
		System.out.print("23 + 10 = ");
		System.out.println (23 + 10);
		
		System.out.print ("23 - 10 = ");
		System.out.println (23 - 10);
		
		System.out.print ("23 * 10 = ");
		System.out.println (23 * 10);
		
		System.out.print ("23 / 10 = ");
		System.out.print (23 / 10 +" remainder " + k);
	
		
	}

}
